<?php echo "<?xml version=\"1.0\" encoding=\"iso-8859-1\"?".">"; ?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" 	"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head><title>Chapter 5 | Dynamic Web Sites | 'switch' method</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1" /></head><body><?php switch ($_GET['id']) {// first case        case "1": $inc = 'pages/about_me.inc.php';        break;// second case        case "2": $inc = 'pages/contact.inc.php';        break;// third case        case "3": $inc = 'pages/links.inc.php';        break;// fourth case        case "4": $inc = 'pages/projects.inc.php';        break;// if no id is specified        default: $inc = 'pages/about_me.inc.php';        break;    }    include ($inc);?></body></html>